CREATE ALGORITHM=UNDEFINED DEFINER=`qa-general`@`%` SQL SECURITY DEFINER VIEW `resumenBonoRiders` AS select `dbr`.`IdRider` AS `IdRider`,`dbr`.`FromDate` AS `fromDate`,`dbr`.`ToDate` AS `ToDate`,count(`dbr`.`IdRiderBono`) AS `CantidadBonos`,group_concat(`dbr`.`IdRiderBono`,',' separator ',') AS `Bonos`,ifnull(sum(`dbr`.`MontoBono`),0) AS `MontoBono`,ifnull(sum(`dbr`.`MontoBonoBruto`),0) AS `MontoBonoBruto`,ifnull(sum(`dbr`.`RetencionHonorariosBono`),0) AS `RetencionHonorariosBono`,ifnull(sum(`dbr`.`MontoBonoNeto`),0) AS `MontoBonoNeto` from `detalleBonoRiders` `dbr` where (`dbr`.`IdEstado` = 3) group by `dbr`.`IdRider`,`dbr`.`FromDate`