select `rb`.`IdRiderBono` AS `IdRiderBono`,`bd`.`AvatarImage` AS `AvatarImage`,`rb`.`FechaPremio` AS `FechaPremio`,`firstDayWeekByDate`(`rb`.`FechaPremio`) AS `FromDate`,`endDayWeekByDate`(`rb`.`FechaPremio`) AS `ToDate`,`rb`.`IdRider` AS `IdRider`,`bd`.`Nombre` AS `Nombre`,`bd`.`PremioMonto` AS `MontoBono`,`n`.`IVA_Reducido` AS `IVA_Reducido`,round(((`bd`.`PremioMonto` * `n`.`IVA_Reducido`) / 100),3) AS `RetencionHonorariosBono`,`bd`.`PremioMonto` AS `MontoBonoBruto`,(select (`MontoBonoBruto` - `RetencionHonorariosBono`)) AS `MontoBonoNeto`,`rb`.`IdBonoDesafio` AS `IdBonoDesafio`,`rb`.`IdRiderDeposito` AS `IdRiderDeposito`,`rb`.`IdEstado` AS `IdEstado` from (((`rider_bono` `rb` left join `rider` `r` on((`r`.`Id_Rider` = `rb`.`IdRider`))) left join `nacionalidad` `n` on((`n`.`Id` = `r`.`IdNacionalidad`))) left join `bono_desafio` `bd` on((`bd`.`IdBonoDesafio` = `rb`.`IdBonoDesafio`))) where ((`bd`.`IdTipoPremio` = 1) and (`rb`.`IdEstado` in (3,5)))